export interface BreadcrumbItem {
    /**
     * Display label for the breadcrumb.
     */
    label: string;
    /**
     * Preferred navigation path.
     */
    route?: string;
    /**
     * Legacy path alias for compatibility.
     */
    path?: string;
    /**
     * Optional material icon name.
     */
    icon?: string;
    /**
     * Whether this item represents the current page.
     */
    isActive?: boolean;
    /**
     * Legacy text alias for compatibility with older components.
     */
    text?: string;
}
