export type ChartType = 'line' | 'bar' | 'column' | 'pie' | 'area' | 'scatter' | 'spline' | 'areaSpline';
export interface ChartDataPoint {
    name?: string;
    y: number;
    x?: number | string;
    color?: string;
    [key: string]: any;
}
export interface ChartSeries {
    name: string;
    data: (number | ChartDataPoint)[];
    type?: ChartType;
    color?: string;
    [key: string]: any;
}
export interface ChartAxis {
    title?: {
        text: string;
    };
    labels?: {
        enabled?: boolean;
        format?: string;
    };
    categories?: string[];
    min?: number;
    max?: number;
    [key: string]: any;
}
export interface ChartTitle {
    text: string;
    align?: 'left' | 'center' | 'right';
    style?: {
        [key: string]: string;
    };
}
export interface ChartOptions {
    chart?: {
        type?: ChartType;
        height?: number;
        width?: number;
        backgroundColor?: string;
        [key: string]: any;
    };
    title?: ChartTitle;
    subtitle?: ChartTitle;
    xAxis?: ChartAxis | ChartAxis[];
    yAxis?: ChartAxis | ChartAxis[];
    series?: ChartSeries[];
    plotOptions?: {
        [key: string]: any;
    };
    legend?: {
        enabled?: boolean;
        [key: string]: any;
    };
    tooltip?: {
        enabled?: boolean;
        [key: string]: any;
    };
    credits?: {
        enabled?: boolean;
    };
    [key: string]: any;
}
